import React, { Component } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert } from 'react-bootstrap';
import { User } from '../models/User';
import ApiClient from '../services/ApiClient';

interface UserProfileState {
    user: User | null;
    loading: boolean;
    editing: boolean;
    formData: {
        name: string;
        email: string;
    };
    error: string | null;
    success: string | null;
}

class UserProfile extends Component<{}, UserProfileState> {
    constructor(props: {}) {
        super(props);
        this.state = {
            user: null,
            loading: true,
            editing: false,
            formData: {
                name: '',
                email: '',
            },
            error: null,
            success: null,
        };
    }

    componentDidMount() {
        this.loadUserProfile();
    }

    loadUserProfile = async () => {
        try {
            this.setState({ loading: true, error: null });
            // Get user data from localStorage (set during login)
            const storedUser = localStorage.getItem('user');
            if (storedUser) {
                const userData = JSON.parse(storedUser);
                const user = new User(userData.name, userData.email, userData.userID);
                this.setState({
                    user,
                    loading: false,
                    formData: {
                        name: user.name,
                        email: user.email,
                    },
                });
            }
        } catch (error) {
            this.setState({
                error: 'Failed to load user profile',
                loading: false,
            });
        }
    };

    handleEditToggle = () => {
        if (this.state.editing) {
            // Cancel editing
            this.setState({
                editing: false,
                formData: {
                    name: this.state.user?.name || '',
                    email: this.state.user?.email || '',
                },
            });
        } else {
            // Enable editing
            this.setState({ editing: true });
        }
    };

    handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        this.setState((prevState) => ({
            formData: {
                ...prevState.formData,
                [name]: value,
            },
        }));
    };

    handleSaveChanges = async () => {
        try {
            this.setState({ error: null, success: null });
            
            const updatedUser = new User(
                this.state.formData.name,
                this.state.formData.email,
                this.state.user?.userID || 0
            );

            // Update user in localStorage
            localStorage.setItem('user', JSON.stringify(updatedUser));

            this.setState({
                user: updatedUser,
                editing: false,
                success: 'Profile updated successfully!',
            });

            // Clear success message after 3 seconds
            setTimeout(() => {
                this.setState({ success: null });
            }, 3000);
        } catch (error) {
            this.setState({
                error: 'Failed to update profile',
            });
        }
    };

    render() {
        const { user, loading, editing, formData, error, success } = this.state;

        if (loading) {
            return (
                <Container className="mt-4">
                    <div className="text-center">Loading profile...</div>
                </Container>
            );
        }

        if (!user) {
            return (
                <Container className="mt-4">
                    <Alert variant="warning">User profile not found</Alert>
                </Container>
            );
        }

        return (
            <Container className="mt-4">
                <Row className="justify-content-center">
                    <Col md={6}>
                        <Card>
                            <Card.Header className="bg-primary text-white">
                                <Card.Title className="mb-0">User Profile</Card.Title>
                            </Card.Header>
                            <Card.Body>
                                {error && <Alert variant="danger">{error}</Alert>}
                                {success && <Alert variant="success">{success}</Alert>}

                                {!editing ? (
                                    // Display Mode
                                    <>
                                        <div className="mb-3">
                                            <strong>Name:</strong>
                                            <p>{user.name}</p>
                                        </div>
                                        <div className="mb-3">
                                            <strong>Email:</strong>
                                            <p>{user.email}</p>
                                        </div>
                                        <div className="mb-3">
                                            <strong>User ID:</strong>
                                            <p>{user.userID}</p>
                                        </div>
                                        <Button
                                            variant="primary"
                                            onClick={this.handleEditToggle}
                                            className="w-100"
                                        >
                                            Edit Profile
                                        </Button>
                                    </>
                                ) : (
                                    // Edit Mode
                                    <>
                                        <Form>
                                            <Form.Group className="mb-3">
                                                <Form.Label>Name</Form.Label>
                                                <Form.Control
                                                    type="text"
                                                    name="name"
                                                    value={formData.name}
                                                    onChange={this.handleInputChange}
                                                    placeholder="Enter your name"
                                                />
                                            </Form.Group>
                                            <Form.Group className="mb-3">
                                                <Form.Label>Email</Form.Label>
                                                <Form.Control
                                                    type="email"
                                                    name="email"
                                                    value={formData.email}
                                                    onChange={this.handleInputChange}
                                                    placeholder="Enter your email"
                                                />
                                            </Form.Group>
                                            <div className="d-flex gap-2">
                                                <Button
                                                    variant="success"
                                                    onClick={this.handleSaveChanges}
                                                    className="flex-grow-1"
                                                >
                                                    Save Changes
                                                </Button>
                                                <Button
                                                    variant="secondary"
                                                    onClick={this.handleEditToggle}
                                                    className="flex-grow-1"
                                                >
                                                    Cancel
                                                </Button>
                                            </div>
                                        </Form>
                                    </>
                                )}
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default UserProfile;
