import React, { Component } from 'react';
import { Container, Row, Col, Card, Button, Form, Alert, Tabs, Tab } from 'react-bootstrap';
import { User } from '../models/User';
import ApiClient from '../services/ApiClient';

interface UserProfileState {
    user: User | null;
    loading: boolean;
    editing: boolean;
    formData: {
        name: string;
        email: string;
    };
    passwordData: {
        currentPassword: string;
        newPassword: string;
        confirmPassword: string;
    };
    error: string | null;
    success: string | null;
    changingPassword: boolean;
}

class UserProfile extends Component<{}, UserProfileState> {
    constructor(props: {}) {
        super(props);
        this.state = {
            user: null,
            loading: true,
            editing: false,
            formData: {
                name: '',
                email: '',
            },
            passwordData: {
                currentPassword: '',
                newPassword: '',
                confirmPassword: '',
            },
            error: null,
            success: null,
            changingPassword: false,
        };
    }

    componentDidMount() {
        this.loadUserProfile();
    }

    loadUserProfile = async () => {
        try {
            this.setState({ loading: true, error: null });
            // Get user data from localStorage (set during login)
            const storedUser = localStorage.getItem('user');
            if (storedUser) {
                const userData = JSON.parse(storedUser);
                const user = new User(userData.name, userData.email, userData.userID);
                this.setState({
                    user,
                    loading: false,
                    formData: {
                        name: user.name,
                        email: user.email,
                    },
                });
            }
        } catch (error) {
            this.setState({
                error: 'Failed to load user profile',
                loading: false,
            });
        }
    };

    handleEditToggle = () => {
        if (this.state.editing) {
            // Cancel editing
            this.setState({
                editing: false,
                formData: {
                    name: this.state.user?.name || '',
                    email: this.state.user?.email || '',
                },
            });
        } else {
            // Enable editing
            this.setState({ editing: true });
        }
    };

    handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        this.setState((prevState) => ({
            formData: {
                ...prevState.formData,
                [name]: value,
            },
        }));
    };

    handlePasswordInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        this.setState((prevState) => ({
            passwordData: {
                ...prevState.passwordData,
                [name]: value,
            },
        }));
    };

    handleSaveChanges = async () => {
        try {
            this.setState({ error: null, success: null });
            
            const updatedUser = new User(
                this.state.formData.name,
                this.state.formData.email,
                this.state.user?.userID || 0
            );

            // Update user in localStorage
            localStorage.setItem('user', JSON.stringify(updatedUser));

            this.setState({
                user: updatedUser,
                editing: false,
                success: 'Profile updated successfully!',
            });

            // Clear success message after 3 seconds
            setTimeout(() => {
                this.setState({ success: null });
            }, 3000);
        } catch (error) {
            this.setState({
                error: 'Failed to update profile',
            });
        }
    };

    handleChangePassword = async () => {
        try {
            this.setState({ error: null, success: null, changingPassword: true });

            const { currentPassword, newPassword, confirmPassword } = this.state.passwordData;

            // Validation
            if (!currentPassword || !newPassword || !confirmPassword) {
                this.setState({
                    error: 'All password fields are required',
                    changingPassword: false,
                });
                return;
            }

            if (newPassword !== confirmPassword) {
                this.setState({
                    error: 'New password and confirm password do not match',
                    changingPassword: false,
                });
                return;
            }

            if (newPassword.length < 6) {
                this.setState({
                    error: 'New password must be at least 6 characters long',
                    changingPassword: false,
                });
                return;
            }

            // Call API to change password
            await ApiClient.changePassword(currentPassword, newPassword);

            this.setState({
                success: 'Password changed successfully!',
                passwordData: {
                    currentPassword: '',
                    newPassword: '',
                    confirmPassword: '',
                },
                changingPassword: false,
            });

            // Clear success message after 3 seconds
            setTimeout(() => {
                this.setState({ success: null });
            }, 3000);
        } catch (error: any) {
            this.setState({
                error: error.response?.data?.message || 'Failed to change password',
                changingPassword: false,
            });
        }
    };

    render() {
        const { user, loading, editing, formData, passwordData, error, success, changingPassword } = this.state;

        if (loading) {
            return (
                <Container className="mt-4">
                    <div className="text-center">Loading profile...</div>
                </Container>
            );
        }

        if (!user) {
            return (
                <Container className="mt-4">
                    <Alert variant="warning">User profile not found</Alert>
                </Container>
            );
        }

        return (
            <Container className="mt-4">
                <Row className="justify-content-center">
                    <Col md={8}>
                        {error && <Alert variant="danger" onClose={() => this.setState({ error: null })} dismissible>{error}</Alert>}
                        {success && <Alert variant="success" onClose={() => this.setState({ success: null })} dismissible>{success}</Alert>}

                        <Tabs defaultActiveKey="profile" className="mb-3">
                            <Tab eventKey="profile" title="Profile Information">
                                <Card>
                                    <Card.Body>
                                        {!editing ? (
                                            // Display Mode
                                            <>
                                                <div className="mb-3">
                                                    <strong>Name:</strong>
                                                    <p>{user.name}</p>
                                                </div>
                                                <div className="mb-3">
                                                    <strong>Email:</strong>
                                                    <p>{user.email}</p>
                                                </div>
                                                <div className="mb-3">
                                                    <strong>User ID:</strong>
                                                    <p>{user.userID}</p>
                                                </div>
                                                <Button
                                                    variant="primary"
                                                    onClick={this.handleEditToggle}
                                                    className="w-100"
                                                >
                                                    Edit Profile
                                                </Button>
                                            </>
                                        ) : (
                                            // Edit Mode
                                            <>
                                                <Form>
                                                    <Form.Group className="mb-3">
                                                        <Form.Label>Name</Form.Label>
                                                        <Form.Control
                                                            type="text"
                                                            name="name"
                                                            value={formData.name}
                                                            onChange={this.handleInputChange}
                                                            placeholder="Enter your name"
                                                        />
                                                    </Form.Group>
                                                    <Form.Group className="mb-3">
                                                        <Form.Label>Email</Form.Label>
                                                        <Form.Control
                                                            type="email"
                                                            name="email"
                                                            value={formData.email}
                                                            onChange={this.handleInputChange}
                                                            placeholder="Enter your email"
                                                        />
                                                    </Form.Group>
                                                    <div className="d-flex gap-2">
                                                        <Button
                                                            variant="success"
                                                            onClick={this.handleSaveChanges}
                                                            className="flex-grow-1"
                                                        >
                                                            Save Changes
                                                        </Button>
                                                        <Button
                                                            variant="secondary"
                                                            onClick={this.handleEditToggle}
                                                            className="flex-grow-1"
                                                        >
                                                            Cancel
                                                        </Button>
                                                    </div>
                                                </Form>
                                            </>
                                        )}
                                    </Card.Body>
                                </Card>
                            </Tab>

                            <Tab eventKey="password" title="Change Password">
                                <Card>
                                    <Card.Body>
                                        <Form>
                                            <Form.Group className="mb-3">
                                                <Form.Label>Current Password</Form.Label>
                                                <Form.Control
                                                    type="password"
                                                    name="currentPassword"
                                                    value={passwordData.currentPassword}
                                                    onChange={this.handlePasswordInputChange}
                                                    placeholder="Enter your current password"
                                                />
                                            </Form.Group>
                                            <Form.Group className="mb-3">
                                                <Form.Label>New Password</Form.Label>
                                                <Form.Control
                                                    type="password"
                                                    name="newPassword"
                                                    value={passwordData.newPassword}
                                                    onChange={this.handlePasswordInputChange}
                                                    placeholder="Enter your new password"
                                                />
                                            </Form.Group>
                                            <Form.Group className="mb-3">
                                                <Form.Label>Confirm Password</Form.Label>
                                                <Form.Control
                                                    type="password"
                                                    name="confirmPassword"
                                                    value={passwordData.confirmPassword}
                                                    onChange={this.handlePasswordInputChange}
                                                    placeholder="Confirm your new password"
                                                />
                                            </Form.Group>
                                            <div className="d-flex gap-2">
                                                <Button
                                                    variant="success"
                                                    onClick={this.handleChangePassword}
                                                    disabled={changingPassword}
                                                    className="flex-grow-1"
                                                >
                                                    {changingPassword ? 'Changing...' : 'Change Password'}
                                                </Button>
                                                <Button
                                                    variant="secondary"
                                                    onClick={() => this.setState({
                                                        passwordData: {
                                                            currentPassword: '',
                                                            newPassword: '',
                                                            confirmPassword: '',
                                                        },
                                                    })}
                                                    className="flex-grow-1"
                                                    disabled={changingPassword}
                                                >
                                                    Clear
                                                </Button>
                                            </div>
                                        </Form>
                                    </Card.Body>
                                </Card>
                            </Tab>
                        </Tabs>
                    </Col>
                </Row>
            </Container>
        );
    }
}

export default UserProfile;
